mapper.select:
select a.PROCESS_CODING, a.PROCESS_NAME, a.PRODUCT_CODING, a.OPERATION_TIME, a.OPERATION_PERSON, a.PROCESSFILEMANAGER_ID, b.PRODUCT_NAME, b.PRODUCT_SPECIFICATIONS from
a,
b where a.PRODUCT_CODING=b.PRODUCT_CODE and a.PROCESSFILEMANAGER_ID = #{PROCESSFILEMANAGER_ID}

select a.PROCESS_CODING, a.PROCESS_NAME, a.PRODUCT_CODING, a.OPERATION_TIME, a.OPERATION_PERSON, a.PROCESSFILEMANAGER_ID, b.PRODUCT_NAME, b.PRODUCT_SPECIFICATIONS from
a,
b where a.PRODUCT_CODING=b.PRODUCT_CODE
and ( a.PROCESS_CODING LIKE '%'+#{pd.keywords}+'%' or a.PROCESS_NAME LIKE '%'+#{pd.keywords}+'%' or a.PRODUCT_CODING LIKE '%'+#{pd.keywords}+'%' or b.PRODUCT_NAME LIKE '%'+#{pd.keywords}+'%' or b.PRODUCT_SPECIFICATIONS LIKE '%'+#{pd.keywords}+'%' or a.OPERATION_PERSON LIKE '%'+#{pd.keywords}+'%'
)
and a.OPERATION_TIME >= #{pd.lastStart}
and a.OPERATION_TIME <= #{pd.lastEnd}
order by OPERATION_TIME desc

select a.PROCESS_CODING, a.PROCESS_NAME, a.PRODUCT_CODING, a.OPERATION_TIME, a.OPERATION_PERSON, a.PROCESSFILEMANAGER_ID, b.PRODUCT_NAME as PRODUCT_NAME, b.PRODUCT_SPECIFICATIONS as PRODUCT_SPECIFICATIONS from
a,
b where a.PRODUCT_CODING=b.PRODUCT_CODE
and ( a.PROCESS_CODING LIKE '%'+#{keywords}+'%' or a.PROCESS_NAME LIKE '%'+#{keywords}+'%' or a.PRODUCT_CODING LIKE '%'+#{keywords}+'%' or b.PRODUCT_NAME LIKE '%'+#{keywords}+'%' or b.PRODUCT_SPECIFICATIONS LIKE '%'+#{keywords}+'%' or a.OPERATION_PERSON LIKE '%'+#{keywords}+'%'
)
and a.OPERATION_TIME >= #{lastStart}
and a.OPERATION_TIME <= #{lastEnd}
